import React, { Component } from 'react'

export default class Signup extends Component {

    constructor(props) 
    {
        super(props);
    
        this.state = 
        {
            name:'',
            email:'',
            city:'',
            course:'',
            phone:''
        }
    }
    

handleName=(element)=>
{
    this.setState({ name: element.target.value  });
}


handleName=(element)=>
{
    this.setState({ name: element.target.value  });
}

//formdata_submition
handleSubmit=(e)=>
{
    alert();
}



    render() {

        var { name, email, city, course, phone }= this.state

        return (<>
            
            <div className="container p-5 w-50 mx-auto">


                    <form onSubmkt={this.handleSubmit.bind(this)}>

                        <div class="form-group">
                            <label>Name</label>
                            <input type="text" class="form-control" value={name} onChange={ this.handleName.bind(this) } />
                        </div>


                        <div class="form-group">
                            <label>Email</label>
                            <input type="email" class="form-control" value={email} />
                        </div>

                        <div class="form-group">
                            <label>Course</label>
                            <input type="text" class="form-control" value={course} />
                        </div>


                        <div class="form-group">
                            <label>City</label>
                            <input type="text" class="form-control" value={city} />
                        </div>

                        <div class="form-group">
                            <label>Phone</label>
                            <input type="number" class="form-control" value={phone} />
                        </div>


                        <div class="form-group">
                            <button type="submit" class="btn btn-primary">Create Account</button>
                        </div>


                    </form>
            </div>
        </>)
    }
}
