import React, { Component } from 'react'
import axios from 'axios'

export default class View extends Component {

    constructor(props) {
        super(props)

    }
    
   
    //Mounting for RESTAPI_Calls
   componentDidMount()
   {
       axios
       .get(`https://jsonplaceholder.typicode.com/users/${this.props.match.params.idno}`)
       .then( 
               (result)=>this.setState({ users:result.data  })  
           )   
   }


    render() {

        //URL_Router_paramter_data 
        var {idno}=this.props.match.params

        return (
            <div>
                
                <h3>VIEW DETAILS  {idno} </h3>

            </div>
        )
    }
}
