import React,{useState, useEffect} from 'react'
import axios from 'axios'
import {Link} from 'react-router-dom'


//GLOBAL_RESTAPI
var url='https://jsonplaceholder.typicode.com/posts'

const Pagination=(props)=> 
{

//State_Object...
var [Posts,setPosts]=useState([]);

//RESTAPI CALLS...
useEffect(async () => {
    await axios.get(url).then( (result)=>setPosts(result.data) )  
}, []);


//Empty array
var numbers=[];

//for Loop
for(var i=1;i<=10;i++)
{
    numbers.push(i);
    //array.push()
}


    return (<>
        <div className="container p-5">
                
                <h2 className="text-center">Dynamic Pagination</h2>

            {/* Pagination */}
                <nav className="">
                    <ul className="pagination">
                       {
                           numbers.map((res,index)=><li className="page-item" key={index}>
                                                        <a className="page-link" href="#">{res}</a>
                                                     </li>)
                       }
                    </ul>
                </nav>
            {/* Pagination */}

            
             {/* APIDATA LISTING */}
             {
                 
             }

             {/* APIDATA LISTING */}



        </div>
    </>)
}

export default Pagination
